#
# Structure for table "admin"
#

DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin` (
  `Id` int(10) NOT NULL AUTO_INCREMENT,
  `username` varchar(20) NOT NULL COMMENT '管理员名称',
  `password` varchar(32) NOT NULL DEFAULT '123456' COMMENT '管理员密码',
  `combjurld` int(10) NOT NULL DEFAULT '1' COMMENT '发表文章权限,0:可编辑，1：不可编辑',
  `updajurld` int(10) NOT NULL DEFAULT '1' COMMENT '修改文章权限0:可修改，1：不可修改',
  `delartijurld` int(10) NOT NULL DEFAULT '1' COMMENT '删除文章权限',
  `isdel` int(10) NOT NULL DEFAULT '0',
  `userjurld` varchar(11) NOT NULL DEFAULT '1' COMMENT '会员管理权限',
  `adminjurld` int(11) NOT NULL DEFAULT '1' COMMENT '管理员管理权限',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='管理员表';

