DROP TABLE IF EXISTS `website`;
CREATE TABLE `website` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `stitle` varchar(72) DEFAULT NULL COMMENT '网站名字',
  `slogo` varchar(100) DEFAULT NULL COMMENT '网站logo图片',
  `surl` char(32) DEFAULT NULL COMMENT '域名',
  `sdescription` varchar(255) DEFAULT NULL COMMENT '网站描述',
  `s_name` varchar(32) DEFAULT NULL COMMENT '联系人',
  `s_phone` varchar(32) DEFAULT NULL COMMENT '电话',
  `s_fax` varchar(255) DEFAULT NULL COMMENT '职业',
  `QQ` int(11) DEFAULT NULL COMMENT 'QQ号',
  `email` text COMMENT '个人简介',
  `s_address` varchar(255) DEFAULT NULL COMMENT '地址',
  `scopyright` varchar(255) DEFAULT NULL COMMENT '底部信息',
  `s_qqu` int(11) DEFAULT NULL COMMENT 'QQ群',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='网站信息表';

